import React, { useEffect, useState } from "react";
import API from "../services/api";

function Monitoring() {
  const [monitoringData, setMonitoringData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Load monitoring data on mount
  useEffect(() => {
    API.get("/monitoring")
      .then((res) => {
        setMonitoringData(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError("Failed to load monitoring data");
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading monitoring data...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Lecture Monitoring Dashboard</h2>
      {monitoringData.length === 0 ? (
        <p>No monitoring data available.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ marginTop: "1rem" }}>
          <thead>
            <tr>
              <th>Course</th>
              <th>Lecturer</th>
              <th>Registered Students</th>
              <th>Students Present</th>
              <th>Attendance %</th>
            </tr>
          </thead>
          <tbody>
            {monitoringData.map((item, idx) => {
              const percentage =
                item.totalRegisteredStudents > 0
                  ? (
                      (item.actualStudentsPresent /
                        item.totalRegisteredStudents) *
                      100
                    ).toFixed(1)
                  : "0";
              return (
                <tr key={idx}>
                  <td>{item.courseName}</td>
                  <td>{item.lecturerName}</td>
                  <td>{item.totalRegisteredStudents}</td>
                  <td>{item.actualStudentsPresent}</td>
                  <td>{percentage}%</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Monitoring;
