import React, { useEffect, useState } from "react";
import API from "../services/api";

function Monitoring() {
  const [monitoringData, setMonitoringData] = useState([]);
  const [form, setForm] = useState({
    name: "",
    surname: "",
    studentNumber: "",
    phone: "",
    signIn: "",
    signOut: "",
  });

  // ✅ Fetch monitoring data
  const fetchMonitoringData = () => {
    API.get("/monitoring")
      .then((res) => setMonitoringData(res.data))
      .catch((err) => console.error("Monitoring fetch error:", err));
  };

  useEffect(() => {
    fetchMonitoringData();
  }, []);

  // ✅ Handle input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ✅ Submit attendance
  const handleSubmit = (e) => {
    e.preventDefault();
    API.post("/attendance", form)
      .then(() => {
        alert("Attendance submitted ✅");
        setForm({
          name: "",
          surname: "",
          studentNumber: "",
          phone: "",
          signIn: "",
          signOut: "",
        });
        fetchMonitoringData(); // refresh data
      })
      .catch((err) => console.error("Attendance submit error:", err));
  };

  return (
    <div>
      <h2>📊 Monitoring Data</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Lecturer</th>
            <th>Total Students</th>
            <th>Present</th>
          </tr>
        </thead>
        <tbody>
          {monitoringData.length > 0 ? (
            monitoringData.map((item, idx) => (
              <tr key={idx}>
                <td>{item.courseName}</td>
                <td>{item.lecturerName}</td>
                <td>{item.totalRegisteredStudents}</td>
                <td>{item.actualStudentsPresent}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No monitoring data available</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>📝 Submit Attendance</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
        />
        <input
          name="surname"
          placeholder="Surname"
          value={form.surname}
          onChange={handleChange}
        />
        <input
          name="studentNumber"
          placeholder="Student Number"
          value={form.studentNumber}
          onChange={handleChange}
        />
        <input
          name="phone"
          placeholder="Phone"
          value={form.phone}
          onChange={handleChange}
        />
        <input
          name="signIn"
          placeholder="Sign In Time"
          value={form.signIn}
          onChange={handleChange}
        />
        <input
          name="signOut"
          placeholder="Sign Out Time"
          value={form.signOut}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Monitoring;
