// frontend/src/pages/Class.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:4000/api/classes";
const USERS_API = "http://localhost:4000/api/users";

const ClassPage = ({ userRole, onClassAdded }) => {
  const [classes, setClasses] = useState([]);
  const [formData, setFormData] = useState({
    faculty: "",
    program: "",
    className: "",
  });
  const [lecturers, setLecturers] = useState([]);
  const [selectedClassId, setSelectedClassId] = useState("");
  const [selectedLecturers, setSelectedLecturers] = useState([]);

  // Fetch classes
  const fetchClasses = async () => {
    try {
      const res = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setClasses(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch classes. Check backend or token.");
    }
  };

  // Fetch lecturers (PL only)
  const fetchLecturers = async () => {
    try {
      const res = await axios.get(USERS_API, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setLecturers(res.data.filter(u => u.role === "LECTURER"));
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchClasses();
    if (userRole === "PL") fetchLecturers();
  }, []);

  const handleChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Add Class
  const handleAddClass = async e => {
    e.preventDefault();
    try {
      const res = await axios.post(API_URL, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setClasses([...classes, res.data]);
      setFormData({ faculty: "", program: "", className: "" });
      alert("Class added successfully!");
      
      if (onClassAdded) onClassAdded();
    } catch (err) {
      console.error(err);
      alert("Failed to add class. Check backend or token.");
    }
  };

  // Assign lecturers to class
  const assignLecturers = async () => {
    if (!selectedClassId || selectedLecturers.length === 0)
      return alert("Select class and lecturers.");
    try {
      await axios.post(
        `${API_URL}/${selectedClassId}/assign`,
        { lecturerIds: selectedLecturers },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("Lecturers assigned successfully!");
      setSelectedClassId("");
      setSelectedLecturers([]);
      fetchClasses();
    } catch (err) {
      console.error(err);
      alert("Failed to assign lecturers.");
    }
  };

  // Faculty dropdown options
  const facultyOptions = ["FICT", "FHCT", "FGDT", "FTCM"];

  // Styles
  const styles = {
    container: { maxWidth: "1000px", margin: "20px auto", fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" },
    card: { padding: "20px", marginBottom: "30px", borderRadius: "10px", backgroundColor: "#f8f9fa", boxShadow: "0px 4px 8px rgba(0,0,0,0.1)" },
    input: { padding: "10px", margin: "8px 0", width: "100%", maxWidth: "400px", borderRadius: "5px", border: "1px solid #ccc" },
    button: { padding: "10px 20px", marginTop: "10px", border: "none", borderRadius: "5px", backgroundColor: "#007bff", color: "white", cursor: "pointer" },
    table: { width: "100%", borderCollapse: "collapse", marginTop: "20px" },
    thtd: { padding: "12px", border: "1px solid #ddd", textAlign: "left" },
    th: { backgroundColor: "#007bff", color: "white" },
    assignBox: { padding: "20px", borderRadius: "10px", backgroundColor: "#e9ecef", marginTop: "20px" },
    select: { padding: "10px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: "100%", maxWidth: "400px" }
  };

  return (
    <div style={styles.container}>
      <h2>Classes</h2>

      {/* PL & PRL: Add Class */}
      {(userRole === "PL" || userRole === "PRL") && (
        <div style={styles.card}>
          <h3>Add New Class</h3>
          <form onSubmit={handleAddClass}>
            <select
              name="faculty"
              value={formData.faculty}
              onChange={handleChange}
              required
              style={styles.select}
            >
              <option value="">Select Faculty</option>
              {facultyOptions.map(fac => (
                <option key={fac} value={fac}>{fac}</option>
              ))}
            </select>
            <input
              type="text"
              name="program"
              placeholder="Program (e.g. BSc IT)"
              value={formData.program}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              type="text"
              name="className"
              placeholder="Class (e.g. BIT1A)"
              value={formData.className}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <button type="submit" style={styles.button}>Add Class</button>
          </form>
        </div>
      )}

      {/* Classes Table */}
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={{ ...styles.thtd, ...styles.th }}>ID</th>
            <th style={styles.thtd}>Faculty</th>
            <th style={styles.thtd}>Program</th>
            <th style={styles.thtd}>Class</th>
            <th style={styles.thtd}>Lecturers</th>
          </tr>
        </thead>
        <tbody>
          {classes.map(c => (
            <tr key={c.id}>
              <td style={styles.thtd}>{c.id}</td>
              <td style={styles.thtd}>{c.faculty}</td>
              <td style={styles.thtd}>{c.program}</td>
              <td style={styles.thtd}>{c.className}</td>
              <td style={styles.thtd}>{c.lecturers?.map(l => l.name).join(", ") || "None"}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* PL Only: Assign Lecturers */}
      {userRole === "PL" && (
        <div style={styles.assignBox}>
          <h3>Assign Lecturers to Class</h3>
          <select
            value={selectedClassId}
            onChange={(e) => setSelectedClassId(e.target.value)}
            style={styles.select}
          >
            <option value="">Select Class</option>
            {classes.map(c => (
              <option key={c.id} value={c.id}>{c.className} - {c.program}</option>
            ))}
          </select>
          <select
            multiple
            value={selectedLecturers}
            onChange={(e) => setSelectedLecturers(Array.from(e.target.selectedOptions, o => parseInt(o.value)))}
            style={styles.select}
          >
            {lecturers.map(l => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>
          <button onClick={assignLecturers} style={styles.button}>Assign Lecturers</button>
        </div>
      )}
    </div>
  );
};

export default ClassPage;
