// frontend/src/pages/Courses.js
import React, { useEffect, useState } from "react";
import API, { setAuth } from "../services/api";

function Courses() {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [facultyFilter, setFacultyFilter] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const [showAddForm, setShowAddForm] = useState(false);
  const [newCourse, setNewCourse] = useState({
    code: "",
    name: "",
    description: "",
    credits: "",
    faculty: ""
  });

  const [showAssignForm, setShowAssignForm] = useState(false);
  const [assignmentData, setAssignmentData] = useState({
    courseId: "",
    lecturerName: ""
  });

  const userRole = localStorage.getItem("role");
  const token = localStorage.getItem("token");

  const isPLorPRL = userRole === "PL" || userRole === "PRL";
  const isPL = userRole === "PL";

  const facultyOptions = ["FICT", "FHCT", "FGDT", "FTCM"];

  useEffect(() => {
    if (!token) {
      setError("Please login to access courses");
      alert("Please login first");
      window.location.href = '/login';
      return;
    }
    
    setAuth(token);
    fetchCourses();
  }, [token]);

  useEffect(() => {
    filterCourses();
  }, [courses, searchTerm, facultyFilter]);

  const fetchCourses = async () => {
    if (!token) {
      setError("No authentication token found");
      return;
    }

    setLoading(true);
    setError("");
    try {
      let endpoint = "/courses";
      if (!isPLorPRL) endpoint = "/courses/my";
      const response = await API.get(endpoint);
      setCourses(response.data || []);
    } catch (err) {
      console.error("❌ Fetch courses error:", err);
      handleFetchError(err);
    } finally {
      setLoading(false);
    }
  };

  const handleFetchError = (err) => {
    let errorMessage = "Failed to fetch courses";
    if (err.response) {
      errorMessage = err.response.data?.error || `Server error: ${err.response.status}`;
    } else if (err.request) {
      errorMessage = "No response from server. Check if backend is running.";
    } else {
      errorMessage = err.message || "Unknown error occurred";
    }
    setError(errorMessage);

    if (err.response?.status === 401) {
      alert("Session expired. Please login again.");
      window.location.href = '/login';
    }
  };

  const filterCourses = () => {
    let filtered = courses;
    if (searchTerm) {
      filtered = filtered.filter(course =>
        course.code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    if (facultyFilter) {
      filtered = filtered.filter(course => course.faculty === facultyFilter);
    }
    setFilteredCourses(filtered);
  };

  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      await API.post("/courses", newCourse);
      alert("✅ Course added successfully!");
      setNewCourse({ code: "", name: "", description: "", credits: "", faculty: "" });
      setShowAddForm(false);
      fetchCourses();
    } catch (err) {
      console.error("❌ Add course error:", err);
      alert(err.response?.data?.error || "Failed to add course");
    }
  };

  const handleAssignLecturer = async (e) => {
    e.preventDefault();
    try {
      await API.post(`/courses/${assignmentData.courseId}/assign`, {
        lecturerName: assignmentData.lecturerName
      });
      alert("✅ Lecturer assigned successfully!");
      setAssignmentData({ courseId: "", lecturerName: "" });
      setShowAssignForm(false);
      fetchCourses();
    } catch (err) {
      console.error("❌ Assign lecturer error:", err);
      alert(err.response?.data?.error || "Failed to assign lecturer");
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm("Are you sure you want to delete this course?")) return;
    try {
      await API.delete(`/courses/${courseId}`);
      alert("✅ Course deleted successfully!");
      fetchCourses();
    } catch (err) {
      console.error("❌ Delete course error:", err);
      alert(err.response?.data?.error || "Failed to delete course");
    }
  };

  // Inline styles
  const styles = {
    container: { padding: "20px", fontFamily: "Arial, sans-serif" },
    header: { display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "15px" },
    headerButtons: { display: "flex", gap: "10px" },
    filters: { display: "flex", gap: "10px", marginBottom: "20px", flexWrap: "wrap" },
    filterInput: { flex: 1, padding: "8px", borderRadius: "5px", border: "1px solid #ccc" },
    filterSelect: { flex: 1, padding: "8px", borderRadius: "5px", border: "1px solid #ccc" },
    resultsCount: { padding: "8px 12px", background: "#f0f0f0", borderRadius: "5px", alignSelf: "center" },
    coursesGrid: { display: "grid", gap: "20px", gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))" },
    courseCard: { border: "1px solid #ddd", borderRadius: "10px", padding: "15px", background: "#fff", display: "flex", flexDirection: "column", justifyContent: "space-between" },
    courseHeader: { display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "10px" },
    courseDetails: { marginBottom: "10px" },
    detailItem: { marginBottom: "5px" },
    label: { fontWeight: "bold", marginRight: "5px" },
    description: { margin: "0" },
    lecturersList: { display: "flex", flexWrap: "wrap", gap: "5px" },
    lecturerBadge: { padding: "2px 6px", background: "#e0e0e0", borderRadius: "5px", fontSize: "0.85rem" },
    courseFooter: { fontSize: "0.75rem", color: "#888" },
    modalOverlay: { position: "fixed", top: 0, left: 0, width: "100%", height: "100%", background: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 1000 },
    modalContent: { background: "#fff", padding: "20px", borderRadius: "10px", width: "90%", maxWidth: "500px" },
    modalHeader: { display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "15px" },
    closeBtn: { background: "none", border: "none", fontSize: "1.5rem", cursor: "pointer" },
    modalBody: { marginBottom: "15px" },
    modalFooter: { display: "flex", justifyContent: "flex-end", gap: "10px" }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2>📚 {isPLorPRL ? "Manage Courses" : "My Courses"}</h2>
        {isPL && (
          <div style={styles.headerButtons}>
            <button onClick={() => setShowAddForm(true)} style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#007bff", color: "#fff", cursor: "pointer" }}>➕ Add Course</button>
            <button onClick={() => setShowAssignForm(true)} style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#28a745", color: "#fff", cursor: "pointer" }}>👥 Assign Lecturer</button>
          </div>
        )}
      </div>

      {/* Search and Filters */}
      <div style={styles.filters}>
        <input
          type="text"
          placeholder="Search courses by code, name, or description..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.filterInput}
        />
        <select value={facultyFilter} onChange={(e) => setFacultyFilter(e.target.value)} style={styles.filterSelect}>
          <option value="">All Faculties</option>
          {facultyOptions.map(fac => <option key={fac} value={fac}>{fac}</option>)}
        </select>
        <div style={styles.resultsCount}>{filteredCourses.length} course(s)</div>
      </div>

      {/* Error Display */}
      {error && <div style={{ padding: "10px", background: "#f8d7da", color: "#842029", borderRadius: "5px", marginBottom: "15px" }}>❌ {error}</div>}

      {/* Loading / Empty / Courses */}
      {loading ? (
        <div style={{ textAlign: "center" }}>
          <div className="spinner-border text-primary" role="status" style={{ marginBottom: "10px" }}></div>
          <p>Loading courses...</p>
        </div>
      ) : filteredCourses.length === 0 ? (
        <div style={{ textAlign: "center", color: "#666" }}>
          <h4>No courses found</h4>
          <p>{isPLorPRL ? "Create your first course above." : "You are not assigned to any courses."}</p>
        </div>
      ) : (
        <div style={styles.coursesGrid}>
          {filteredCourses.map(course => (
            <div key={course.id} style={styles.courseCard}>
              <div style={styles.courseHeader}>
                <h5>{course.code} - {course.name}</h5>
                {isPL && <button onClick={() => handleDeleteCourse(course.id)} style={{ background: "#dc3545", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>🗑️</button>}
              </div>
              <div style={styles.courseDetails}>
                <div style={styles.detailItem}><span style={styles.label}>Faculty:</span> {course.faculty}</div>
                {course.credits && <div style={styles.detailItem}><span style={styles.label}>Credits:</span> {course.credits}</div>}
                {course.description && <div style={{ ...styles.detailItem, marginTop: "5px" }}><span style={styles.label}>Description:</span> <p style={styles.description}>{course.description}</p></div>}
                {course.lecturers?.length > 0 && <div style={{ ...styles.detailItem, marginTop: "5px" }}><span style={styles.label}>Assigned Lecturers:</span>
                  <div style={styles.lecturersList}>{course.lecturers.map(lect => <span key={lect.id} style={styles.lecturerBadge}>{lect.name}</span>)}</div>
                </div>}
              </div>
              <div style={styles.courseFooter}>
                Created: {new Date(course.createdAt).toLocaleDateString()}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Add Course Modal */}
      {showAddForm && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <div style={styles.modalHeader}>
              <h5>Add New Course</h5>
              <button style={styles.closeBtn} onClick={() => setShowAddForm(false)}>×</button>
            </div>
            <form onSubmit={handleAddCourse}>
              <div style={styles.modalBody}>
                <input placeholder="Course Code" value={newCourse.code} onChange={e => setNewCourse({...newCourse, code: e.target.value})} style={{ width: "100%", padding: "6px", marginBottom: "8px" }} required />
                <input placeholder="Course Name" value={newCourse.name} onChange={e => setNewCourse({...newCourse, name: e.target.value})} style={{ width: "100%", padding: "6px", marginBottom: "8px" }} required />
                <select value={newCourse.faculty} onChange={e => setNewCourse({...newCourse, faculty: e.target.value})} style={{ width: "100%", padding: "6px", marginBottom: "8px" }} required>
                  <option value="">Select Faculty</option>
                  {facultyOptions.map(fac => <option key={fac} value={fac}>{fac}</option>)}
                </select>
                <input placeholder="Credits" type="number" value={newCourse.credits} onChange={e => setNewCourse({...newCourse, credits: e.target.value})} style={{ width: "100%", padding: "6px", marginBottom: "8px" }} />
                <textarea placeholder="Description" value={newCourse.description} onChange={e => setNewCourse({...newCourse, description: e.target.value})} style={{ width: "100%", padding: "6px", marginBottom: "8px" }} rows="3" />
              </div>
              <div style={styles.modalFooter}>
                <button type="button" onClick={() => setShowAddForm(false)} style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#6c757d", color: "#fff" }}>Cancel</button>
                <button type="submit" style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#007bff", color: "#fff" }}>Add Course</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Assign Lecturer Modal */}
      {showAssignForm && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <div style={styles.modalHeader}>
              <h5>Assign Lecturer to Course</h5>
              <button style={styles.closeBtn} onClick={() => setShowAssignForm(false)}>×</button>
            </div>
            <form onSubmit={handleAssignLecturer}>
              <div style={styles.modalBody}>
                <select
                  value={assignmentData.courseId}
                  onChange={e => setAssignmentData({...assignmentData, courseId: e.target.value})}
                  style={{ width: "100%", padding: "6px", marginBottom: "8px" }}
                  required
                >
                  <option value="">Select Course</option>
                  {courses.map(course => <option key={course.id} value={course.id}>{course.code} - {course.name}</option>)}
                </select>

                <input
                  type="text"
                  placeholder="Enter Lecturer Name"
                  value={assignmentData.lecturerName}
                  onChange={e => setAssignmentData({...assignmentData, lecturerName: e.target.value})}
                  style={{ width: "100%", padding: "6px", marginBottom: "8px" }}
                  required
                />
              </div>
              <div style={styles.modalFooter}>
                <button type="button" onClick={() => setShowAssignForm(false)} style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#6c757d", color: "#fff" }}>Cancel</button>
                <button type="submit" style={{ padding: "6px 12px", borderRadius: "5px", border: "none", background: "#007bff", color: "#fff" }}>Assign Lecturer</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Courses;
