import React from 'react';
import { Link } from 'react-router-dom';

export default function Dashboard() {
  // Inline Styles
  const styles = {
    dashboardContainer: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    },
    dashboardCard: {
      background: 'linear-gradient(135deg, #2c3e50 0%, #3498db 100%)',
      borderRadius: '12px',
      padding: '40px',
      color: 'white',
      textAlign: 'center',
      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)'
    },
    dashboardTitle: {
      margin: '0 0 20px 0',
      fontSize: '2rem',
      fontWeight: '600'
    },
    dashboardText: {
      margin: '15px 0',
      fontSize: '1.1rem',
      lineHeight: '1.6',
      opacity: '0.9'
    },
    highlightText: {
      color: '#f39c12',
      fontWeight: 'bold'
    },
    registerLink: {
      color: '#f3f5f6',
      fontWeight: 'bold',
      textDecoration: 'none'
    },
    // Mobile responsive styles
    mobileStyles: {
      '@media (max-width: 768px)': {
        dashboardContainer: {
          padding: '15px'
        },
        dashboardCard: {
          padding: '25px'
        },
        dashboardTitle: {
          fontSize: '1.5rem'
        }
      }
    }
  };

  return (
    <div style={styles.dashboardContainer}>
      {/* Welcome Card */}
      <div style={styles.dashboardCard}>
        <h2 style={styles.dashboardTitle}>LUCT Lecture Reporting System</h2>
        <p style={styles.dashboardText}>
          Submit and review lecture reports easily. Use the Register option to set up accounts for students, lecturers, PRLs, or PLs.
        </p>
        <p style={styles.dashboardText}>
          Default administrator login: <span style={styles.highlightText}>admin@luct.edu</span> / <span style={styles.highlightText}>password</span> (role: PL)
        </p>

        {/* Register link */}
        <p style={styles.dashboardText}>
          Don't have an account?{" "}
          <Link to="/register" style={styles.registerLink}>
            Register here
          </Link>
        </p>
      </div>
    </div>
  );
}
