// frontend/src/pages/Course.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:4000/courses"; 
const USERS_API = "http://localhost:4000/users"; 

const Course = ({ userRole }) => {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    courseCode: "",
    courseName: "",
    facultyName: "",
    stream: "",
  });
  const [lecturers, setLecturers] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState("");
  const [selectedLecturers, setSelectedLecturers] = useState([]);

  // Fetch courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch lecturers
  const fetchLecturers = async () => {
    try {
      const res = await axios.get(USERS_API, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setLecturers(res.data.filter(u => u.role === "LECTURER"));
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchCourses();
    if (userRole === "PL") fetchLecturers();
  }, []);

  // Handle input change
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Add new course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(API_URL, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses([...courses, res.data]);
      setFormData({ courseCode: "", courseName: "", facultyName: "", stream: "" });
      alert("Course added successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to add course");
    }
  };

  // Assign lecturers
  const assignLecturers = async () => {
    if (!selectedCourseId || selectedLecturers.length === 0) return alert("Select course and lecturers.");
    try {
      await axios.post(`${API_URL}/${selectedCourseId}/assign`, { lecturerIds: selectedLecturers }, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("Lecturers assigned successfully!");
      setSelectedCourseId("");
      setSelectedLecturers([]);
      fetchCourses();
    } catch (err) {
      console.error(err);
      alert("Failed to assign lecturers");
    }
  };

  return (
    <div>
      <h2>Courses</h2>

      {/* Add Course Form */}
      {userRole === "PL" && (
        <div style={{ marginBottom: "30px" }}>
          <h3>Add New Course</h3>
          <form onSubmit={handleAddCourse}>
            <input
              type="text"
              name="courseCode"
              placeholder="Course Code"
              value={formData.courseCode}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="courseName"
              placeholder="Course Name"
              value={formData.courseName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="facultyName"
              placeholder="Faculty"
              value={formData.facultyName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="stream"
              placeholder="Stream (optional)"
              value={formData.stream}
              onChange={handleChange}
            />
            <button type="submit">Add Course</button>
          </form>
        </div>
      )}

      {/* Courses Table */}
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Faculty</th>
            <th>Stream</th>
            <th>Lecturers</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(c => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.courseCode}</td>
              <td>{c.courseName}</td>
              <td>{c.facultyName}</td>
              <td>{c.stream || "-"}</td>
              <td>{c.lecturers.map(l => l.name).join(", ")}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Assign Lecturers */}
      {userRole === "PL" && (
        <div style={{ marginTop: "20px" }}>
          <h3>Assign Lecturers to Course</h3>
          <select value={selectedCourseId} onChange={(e) => setSelectedCourseId(e.target.value)}>
            <option value="">Select Course</option>
            {courses.map(c => (
              <option key={c.id} value={c.id}>{c.courseCode} - {c.courseName}</option>
            ))}
          </select>
          <select
            multiple
            value={selectedLecturers}
            onChange={(e) =>
              setSelectedLecturers(
                Array.from(e.target.selectedOptions, o => parseInt(o.value))
              )
            }
          >
            {lecturers.map(l => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>
          <button onClick={assignLecturers}>Assign Lecturers</button>
        </div>
      )}
    </div>
  );
};

export default Course;
