// frontend/src/components/Course.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:4000/courses"; // adjust to your backend

const Course = ({ userRole }) => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    courseCode: "",
    courseName: "",
    facultyName: "",
    stream: "",
  });
  const [assignData, setAssignData] = useState({ courseId: "", lecturerIds: "" });

  // Fetch all courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get(API_URL, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setCourses(res.data);
    } catch (err) {
      console.error("Fetch courses error:", err);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Handle input change
  const handleChange = (e) => setNewCourse({ ...newCourse, [e.target.name]: e.target.value });

  // Create course (PL only)
  const createCourse = async () => {
    try {
      const res = await axios.post(API_URL, newCourse, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setCourses([...courses, res.data]);
      setNewCourse({ courseCode: "", courseName: "", facultyName: "", stream: "" });
    } catch (err) {
      console.error("Create course error:", err);
    }
  };

  // Update course (PL only)
  const updateCourse = async (id, updatedData) => {
    try {
      const res = await axios.put(`${API_URL}/${id}`, updatedData, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setCourses(courses.map(c => (c.id === id ? res.data : c)));
    } catch (err) {
      console.error("Update course error:", err);
    }
  };

  // Delete course (PL only)
  const deleteCourse = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setCourses(courses.filter(c => c.id !== id));
    } catch (err) {
      console.error("Delete course error:", err);
    }
  };

  // Assign lecturers (PL only)
  const assignLecturers = async () => {
    try {
      const lecturerIdsArray = assignData.lecturerIds.split(",").map(id => parseInt(id.trim()));
      await axios.post(`${API_URL}/${assignData.courseId}/assign`, { lecturerIds: lecturerIdsArray }, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      alert("Lecturers assigned successfully!");
      setAssignData({ courseId: "", lecturerIds: "" });
      fetchCourses(); // refresh
    } catch (err) {
      console.error("Assign lecturers error:", err);
    }
  };

  return (
    <div>
      <h2>Courses</h2>

      {/* PL Only: Add Course */}
      {userRole === "PL" && (
        <div style={{ marginBottom: "20px" }}>
          <h3>Add New Course</h3>
          <input name="courseCode" placeholder="Course Code" value={newCourse.courseCode} onChange={handleChange} />
          <input name="courseName" placeholder="Course Name" value={newCourse.courseName} onChange={handleChange} />
          <input name="facultyName" placeholder="Faculty" value={newCourse.facultyName} onChange={handleChange} />
          <input name="stream" placeholder="Stream" value={newCourse.stream} onChange={handleChange} />
          <button onClick={createCourse}>Add Course</button>
        </div>
      )}

      {/* Course List */}
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Faculty</th>
            <th>Stream</th>
            <th>Lecturers</th>
            {userRole === "PL" && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>{course.id}</td>
              <td>{course.courseCode}</td>
              <td>{course.courseName}</td>
              <td>{course.facultyName}</td>
              <td>{course.stream || "-"}</td>
              <td>{course.lecturers.map(l => l.name).join(", ")}</td>
              {userRole === "PL" && (
                <td>
                  <button onClick={() => deleteCourse(course.id)}>Delete</button>
                  {/* For simplicity, update can be handled with prompt */}
                  <button
                    onClick={() => {
                      const newName = prompt("New course name:", course.courseName);
                      if (newName) updateCourse(course.id, { courseName: newName });
                    }}
                  >
                    Update
                  </button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>

      {/* PL Only: Assign Lecturers */}
      {userRole === "PL" && (
        <div style={{ marginTop: "20px" }}>
          <h3>Assign Lecturers to Course</h3>
          <input
            placeholder="Course ID"
            value={assignData.courseId}
            onChange={(e) => setAssignData({ ...assignData, courseId: e.target.value })}
          />
          <input
            placeholder="Lecturer IDs (comma separated)"
            value={assignData.lecturerIds}
            onChange={(e) => setAssignData({ ...assignData, lecturerIds: e.target.value })}
          />
          <button onClick={assignLecturers}>Assign</button>
        </div>
      )}
    </div>
  );
};

export default Course;
