import React, { useEffect, useState } from "react";
import API from "../services/api";

function Monitoring() {
  const [monitoringData, setMonitoringData] = useState([]);
  const [form, setForm] = useState({
    name: "",
    surname: "",
    studentNumber: "",
    phone: "",
    signIn: "",
    signOut: "",
  });

  // Load monitoring data on mount
  useEffect(() => {
    API.get("/monitoring")
      .then((res) => setMonitoringData(res.data))
      .catch((err) => console.error(err));
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Submit attendance
  const handleSubmit = (e) => {
    e.preventDefault();
    API.post("/attendance", form)
      .then((res) => {
        alert("Attendance submitted ✅");
        setForm({
          name: "",
          surname: "",
          studentNumber: "",
          phone: "",
          signIn: "",
          signOut: "",
        });
        // Refresh monitoring data
        return API.get("/monitoring");
      })
      .then((res) => setMonitoringData(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h2>Monitoring Data</h2>
      <ul>
        {monitoringData.map((item, idx) => (
          <li key={idx}>
            {item.courseName} — {item.lecturerName} — Students Present:{" "}
            {item.actualStudentsPresent}/{item.totalRegisteredStudents}
          </li>
        ))}
      </ul>

      <h2>Submit Attendance</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
        />
        <input
          name="surname"
          placeholder="Surname"
          value={form.surname}
          onChange={handleChange}
        />
        <input
          name="studentNumber"
          placeholder="Student Number"
          value={form.studentNumber}
          onChange={handleChange}
        />
        <input
          name="phone"
          placeholder="Phone"
          value={form.phone}
          onChange={handleChange}
        />
        <input
          name="signIn"
          placeholder="Sign In"
          value={form.signIn}
          onChange={handleChange}
        />
        <input
          name="signOut"
          placeholder="Sign Out"
          value={form.signOut}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Monitoring;
