// frontend/src/pages/Courses.js
import React, { useEffect, useState } from "react";
import API, { setAuth } from "../services/api";
import '../styles/courses.css';  // Adjust the path if necessary

function Courses() {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [facultyFilter, setFacultyFilter] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Form states
  const [showAddForm, setShowAddForm] = useState(false);
  const [newCourse, setNewCourse] = useState({
    code: "",
    name: "",
    description: "",
    credits: "",
    faculty: ""
  });

  const [showAssignForm, setShowAssignForm] = useState(false);
  const [assignmentData, setAssignmentData] = useState({
    courseId: "",
    lecturerId: ""
  });

  const [lecturers, setLecturers] = useState([]);

  const userRole = localStorage.getItem("role");
  const token = localStorage.getItem("token");

  // Check if user is PL or PRL
  const isPLorPRL = userRole === "PL" || userRole === "PRL";
  const isPL = userRole === "PL";

  useEffect(() => {
    if (!token) {
      setError("Please login to access courses");
      alert("Please login first");
      window.location.href = '/login';
      return;
    }
    
    setAuth(token);
    fetchCourses();
    if (isPL) {
      fetchLecturers();
    }
  }, [token, isPL]);

  useEffect(() => {
    filterCourses();
  }, [courses, searchTerm, facultyFilter]);

  // Fetch courses
  const fetchCourses = async () => {
    if (!token) {
      setError("No authentication token found");
      return;
    }

    setLoading(true);
    setError("");
    
    try {
      let endpoint = "/courses";
      
      // If user is lecturer, fetch only their courses
      if (!isPLorPRL) {
        endpoint = "/courses/my";
      }
      
      const response = await API.get(endpoint);
      setCourses(response.data || []);
      
    } catch (err) {
      console.error("❌ Fetch courses error:", err);
      handleFetchError(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch lecturers for PL
  const fetchLecturers = async () => {
    try {
      const response = await API.get("/users/lecturers");
      setLecturers(response.data || []);
    } catch (err) {
      console.error("❌ Fetch lecturers error:", err);
    }
  };

  const handleFetchError = (err) => {
    let errorMessage = "Failed to fetch courses";
    
    if (err.response) {
      errorMessage = err.response.data?.error || `Server error: ${err.response.status}`;
    } else if (err.request) {
      errorMessage = "No response from server. Check if backend is running.";
    } else {
      errorMessage = err.message || "Unknown error occurred";
    }
    
    setError(errorMessage);
    
    if (err.response?.status === 401) {
      alert("Session expired. Please login again.");
      window.location.href = '/login';
    }
  };

  const filterCourses = () => {
    let filtered = courses;

    if (searchTerm) {
      filtered = filtered.filter(course =>
        course.code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (facultyFilter) {
      filtered = filtered.filter(course => course.faculty === facultyFilter);
    }

    setFilteredCourses(filtered);
  };

  // Add new course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      await API.post("/courses", newCourse);
      alert("✅ Course added successfully!");
      setNewCourse({
        code: "",
        name: "",
        description: "",
        credits: "",
        faculty: ""
      });
      setShowAddForm(false);
      fetchCourses();
    } catch (err) {
      console.error("❌ Add course error:", err);
      alert(err.response?.data?.error || "Failed to add course");
    }
  };

  // Assign lecturer to course
  const handleAssignLecturer = async (e) => {
    e.preventDefault();
    try {
      await API.post(`/courses/${assignmentData.courseId}/assign`, {
        lecturerId: assignmentData.lecturerId
      });
      alert("✅ Lecturer assigned successfully!");
      setAssignmentData({ courseId: "", lecturerId: "" });
      setShowAssignForm(false);
      fetchCourses();
    } catch (err) {
      console.error("❌ Assign lecturer error:", err);
      alert(err.response?.data?.error || "Failed to assign lecturer");
    }
  };

  // Delete course
  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm("Are you sure you want to delete this course?")) return;
    
    try {
      await API.delete(`/courses/${courseId}`);
      alert("✅ Course deleted successfully!");
      fetchCourses();
    } catch (err) {
      console.error("❌ Delete course error:", err);
      alert(err.response?.data?.error || "Failed to delete course");
    }
  };

  const facultyOptions = ["FICT", "FHCT", "FGDT", "FTCM"];

  return (
    <div className="courses-container">
      <div className="page-header">
        <h2>📚 {isPLorPRL ? "Manage Courses" : "My Courses"}</h2>
        {isPL && (
          <div className="header-actions">
            <button 
              onClick={() => setShowAddForm(true)} 
              className="btn btn-primary"
            >
              ➕ Add Course
            </button>
            <button 
              onClick={() => setShowAssignForm(true)} 
              className="btn btn-success"
            >
              👥 Assign Lecturer
            </button>
          </div>
        )}
      </div>

      {/* Search and Filters */}
      <div className="filters-section">
        <div className="row g-3">
          <div className="col-md-5">
            <input
              type="text"
              className="form-control"
              placeholder="Search courses by code, name, or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="col-md-3">
            <select
              className="form-select"
              value={facultyFilter}
              onChange={(e) => setFacultyFilter(e.target.value)}
            >
              <option value="">All Faculties</option>
              {facultyOptions.map(faculty => (
                <option key={faculty} value={faculty}>{faculty}</option>
              ))}
            </select>
          </div>
          <div className="col-md-2">
            <button onClick={fetchCourses} className="btn btn-outline-secondary w-100">
              🔄 Refresh
            </button>
          </div>
          <div className="col-md-2">
            <div className="results-count">
              {filteredCourses.length} course(s)
            </div>
          </div>
        </div>
      </div>

      {/* Add Course Modal */}
      {showAddForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h5>Add New Course</h5>
              <button onClick={() => setShowAddForm(false)} className="close-btn">×</button>
            </div>
            <form onSubmit={handleAddCourse}>
              <div className="modal-body">
                <div className="row g-3">
                  <div className="col-md-6">
                    <label>Course Code *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={newCourse.code}
                      onChange={(e) => setNewCourse({...newCourse, code: e.target.value})}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label>Course Name *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={newCourse.name}
                      onChange={(e) => setNewCourse({...newCourse, name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label>Faculty *</label>
                    <select
                      className="form-select"
                      value={newCourse.faculty}
                      onChange={(e) => setNewCourse({...newCourse, faculty: e.target.value})}
                      required
                    >
                      <option value="">Select Faculty</option>
                      {facultyOptions.map(faculty => (
                        <option key={faculty} value={faculty}>{faculty}</option>
                      ))}
                    </select>
                  </div>
                  <div className="col-md-6">
                    <label>Credits</label>
                    <input
                      type="number"
                      className="form-control"
                      value={newCourse.credits}
                      onChange={(e) => setNewCourse({...newCourse, credits: e.target.value})}
                    />
                  </div>
                  <div className="col-12">
                    <label>Description</label>
                    <textarea
                      className="form-control"
                      rows="3"
                      value={newCourse.description}
                      onChange={(e) => setNewCourse({...newCourse, description: e.target.value})}
                    />
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" onClick={() => setShowAddForm(false)} className="btn btn-secondary">
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Add Course
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Assign Lecturer Modal */}
      {showAssignForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h5>Assign Lecturer to Course</h5>
              <button onClick={() => setShowAssignForm(false)} className="close-btn">×</button>
            </div>
            <form onSubmit={handleAssignLecturer}>
              <div className="modal-body">
                <div className="row g-3">
                  <div className="col-12">
                    <label>Select Course *</label>
                    <select
                      className="form-select"
                      value={assignmentData.courseId}
                      onChange={(e) => setAssignmentData({...assignmentData, courseId: e.target.value})}
                      required
                    >
                      <option value="">Select Course</option>
                      {courses.map(course => (
                        <option key={course.id} value={course.id}>
                          {course.code} - {course.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="col-12">
                    <label>Select Lecturer *</label>
                    <select
                      className="form-select"
                      value={assignmentData.lecturerId}
                      onChange={(e) => setAssignmentData({...assignmentData, lecturerId: e.target.value})}
                      required
                    >
                      <option value="">Select Lecturer</option>
                      {lecturers.map(lecturer => (
                        <option key={lecturer.id} value={lecturer.id}>
                          {lecturer.name} {lecturer.surname}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" onClick={() => setShowAssignForm(false)} className="btn btn-secondary">
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Assign Lecturer
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Error Display */}
      {error && (
        <div className="alert alert-danger">
          <strong>❌ Error:</strong> {error}
        </div>
      )}

      {/* Courses Grid */}
      {loading ? (
        <div className="loading-state">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading courses...</p>
        </div>
      ) : filteredCourses.length === 0 ? (
        <div className="empty-state">
          <h4>No courses found</h4>
          <p>{isPLorPRL ? "Create your first course above." : "You are not assigned to any courses."}</p>
        </div>
      ) : (
        <div className="courses-grid">
          {filteredCourses.map(course => (
            <div key={course.id} className="course-card">
              <div className="course-header">
                <h5>{course.code} - {course.name}</h5>
                {isPL && (
                  <button
                    onClick={() => handleDeleteCourse(course.id)}
                    className="btn btn-danger btn-sm"
                    title="Delete course"
                  >
                    🗑️
                  </button>
                )}
              </div>
              
              <div className="course-details">
                <div className="detail-item">
                  <span className="label">Faculty:</span>
                  <span className="value">{course.faculty}</span>
                </div>
                {course.credits && (
                  <div className="detail-item">
                    <span className="label">Credits:</span>
                    <span className="value">{course.credits}</span>
                  </div>
                )}
                {course.description && (
                  <div className="detail-item full-width">
                    <span className="label">Description:</span>
                    <p className="description">{course.description}</p>
                  </div>
                )}
                {course.lecturers && course.lecturers.length > 0 && (
                  <div className="detail-item full-width">
                    <span className="label">Assigned Lecturers:</span>
                    <div className="lecturers-list">
                      {course.lecturers.map(lecturer => (
                        <span key={lecturer.id} className="lecturer-badge">
                          {lecturer.name} {lecturer.surname}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
              
              <div className="course-footer">
                <small className="text-muted">
                  Created: {new Date(course.createdAt).toLocaleDateString()}
                </small>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Courses;
