import React, { useEffect, useState } from "react";
import API from "../services/api";

function Classes() {
  const [classes, setClasses] = useState([]);
  const [formData, setFormData] = useState({
    courseName: "",
    courseCode: "",
  });

  // ‚úÖ Pull lecturer info directly from localStorage
  const lecturerId = localStorage.getItem("lecturerId");
  const lecturerName = localStorage.getItem("lecturerName");

  // Fetch lecturer's classes
  const fetchClasses = () => {
    if (!lecturerId) {
      console.error("‚ùå No lecturerId found in localStorage");
      return;
    }

    API.get(`/classes/${lecturerId}`)
      .then((res) => setClasses(res.data))
      .catch((err) => console.error("Fetch classes error:", err));
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add class
  const handleAddClass = (e) => {
    e.preventDefault();
    if (!formData.courseName.trim()) return;

    API.post("/classes", {
      courseName: formData.courseName,
      courseCode: formData.courseCode,
      lecturerId,
      lecturerName,
    })
      .then(() => {
        setFormData({ courseName: "", courseCode: "" });
        fetchClasses();
      })
      .catch((err) => console.error("Add class error:", err));
  };

  // Delete class
  const handleDeleteClass = (id) => {
    API.delete(`/classes/${id}`)
      .then(() => fetchClasses())
      .catch((err) => console.error("Delete class error:", err));
  };

  return (
    <div className="card">
      <h2 style={{ padding: "10px 0" }}>üìö My Classes</h2>

      {/* Add class form */}
      <form onSubmit={handleAddClass} style={{ marginBottom: "15px" }}>
        <input
          type="text"
          name="courseName"
          value={formData.courseName}
          onChange={handleChange}
          placeholder="Enter class/course name"
          style={{ padding: "6px", marginRight: "10px", width: "200px" }}
        />
        <input
          type="text"
          name="courseCode"
          value={formData.courseCode}
          onChange={handleChange}
          placeholder="Enter course code"
          style={{ padding: "6px", marginRight: "10px", width: "150px" }}
        />
        <button
          type="submit"
          style={{
            padding: "6px 12px",
            borderRadius: "6px",
            border: "none",
            background: "linear-gradient(to right, #11998e, #38ef7d)",
            color: "white",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          ‚ûï Add Class
        </button>
      </form>

      {/* Classes table */}
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          background: "white",
          borderRadius: "10px",
          overflow: "hidden",
        }}
      >
        <thead style={{ background: "linear-gradient(to right, #11998e, #38ef7d)", color: "white" }}>
          <tr>
            <th style={{ padding: "10px" }}>Course Name</th>
            <th style={{ padding: "10px" }}>Course Code</th>
            <th style={{ padding: "10px" }}>Lecturer Name</th>
            <th style={{ padding: "10px" }}>Created At</th>
            <th style={{ padding: "10px" }}>Updated At</th>
            <th style={{ padding: "10px", textAlign: "center" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {classes.length > 0 ? (
            classes.map((cls) => (
              <tr key={cls.id}>
                <td style={{ padding: "8px" }}>{cls.courseName}</td>
                <td style={{ padding: "8px" }}>{cls.courseCode}</td>
                <td style={{ padding: "8px" }}>{cls.lecturerName}</td>
                <td style={{ padding: "8px" }}>{new Date(cls.createdAt).toLocaleString()}</td>
                <td style={{ padding: "8px" }}>{new Date(cls.updatedAt).toLocaleString()}</td>
                <td style={{ textAlign: "center" }}>
                  <button
                    onClick={() => handleDeleteClass(cls.id)}
                    style={{
                      padding: "6px 12px",
                      borderRadius: "6px",
                      border: "none",
                      background: "linear-gradient(to right, #ff512f, #dd2476)",
                      color: "white",
                      cursor: "pointer",
                      fontWeight: "bold",
                    }}
                  >
                    ‚ùå Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" style={{ textAlign: "center", padding: "10px", color: "gray" }}>
                No classes added yet
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Classes;
