import React, { useEffect, useState } from "react";
import API from "../services/api";

function Monitoring() {
  const [monitoringData, setMonitoringData] = useState([]);
  const [newRow, setNewRow] = useState({
    courseName: "",
    lecturerName: "",
    totalRegisteredStudents: "",
    actualStudentsPresent: "",
  });

  const [form, setForm] = useState({
    name: "",
    surname: "",
    studentNumber: "",
    phone: "",
    signIn: "",
    signOut: "",
  });

  // ✅ Fetch monitoring data
  const fetchMonitoringData = () => {
    API.get("/monitoring")
      .then((res) => setMonitoringData(res.data))
      .catch((err) => console.error("Monitoring fetch error:", err));
  };

  useEffect(() => {
    fetchMonitoringData();
  }, []);

  // ✅ Handle input changes for monitoring row
  const handleRowChange = (e) => {
    setNewRow({ ...newRow, [e.target.name]: e.target.value });
  };

  // ✅ Submit new monitoring row
  const handleAddRow = (e) => {
    e.preventDefault();
    API.post("/monitoring", newRow)
      .then(() => {
        alert("Monitoring row added ✅");
        setNewRow({
          courseName: "",
          lecturerName: "",
          totalRegisteredStudents: "",
          actualStudentsPresent: "",
        });
        fetchMonitoringData();
      })
      .catch((err) => console.error("Add row error:", err));
  };

  // ✅ Handle attendance input changes
  const handleAttendanceChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ✅ Submit attendance
  const handleAttendanceSubmit = (e) => {
    e.preventDefault();
    API.post("/attendance", form)
      .then(() => {
        alert("Attendance submitted ✅");
        setForm({
          name: "",
          surname: "",
          studentNumber: "",
          phone: "",
          signIn: "",
          signOut: "",
        });
        fetchMonitoringData(); // refresh monitoring
      })
      .catch((err) => console.error("Attendance submit error:", err));
  };

  return (
    <div>
      <h2>📊 Monitoring Data</h2>
      <form onSubmit={handleAddRow}>
        <table
          border="1"
          cellPadding="8"
          style={{ borderCollapse: "collapse", width: "100%" }}
        >
          <thead>
            <tr>
              <th>Course</th>
              <th>Lecturer</th>
              <th>Total Students</th>
              <th>Present</th>
            </tr>
          </thead>
          <tbody>
            {/* 🔽 Input row under headers */}
            <tr>
              <td>
                <input
                  type="text"
                  name="courseName"
                  value={newRow.courseName}
                  onChange={handleRowChange}
                  placeholder="Enter course"
                />
              </td>
              <td>
                <input
                  type="text"
                  name="lecturerName"
                  value={newRow.lecturerName}
                  onChange={handleRowChange}
                  placeholder="Enter lecturer"
                />
              </td>
              <td>
                <input
                  type="number"
                  name="totalRegisteredStudents"
                  value={newRow.totalRegisteredStudents}
                  onChange={handleRowChange}
                  placeholder="Total"
                />
              </td>
              <td>
                <input
                  type="number"
                  name="actualStudentsPresent"
                  value={newRow.actualStudentsPresent}
                  onChange={handleRowChange}
                  placeholder="Present"
                />
              </td>
            </tr>

            {/* 🔽 Existing data rows */}
            {monitoringData.length > 0 ? (
              monitoringData.map((item, idx) => (
                <tr key={idx}>
                  <td>{item.courseName}</td>
                  <td>{item.lecturerName}</td>
                  <td>{item.totalRegisteredStudents}</td>
                  <td>{item.actualStudentsPresent}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No monitoring data available</td>
              </tr>
            )}
          </tbody>
        </table>
        <button type="submit" style={{ marginTop: "10px" }}>
          ➕ Add Row
        </button>
      </form>

      {/* ✅ Attendance Form */}
      <h2 style={{ marginTop: "30px" }}>📝 Submit Attendance</h2>
      <form onSubmit={handleAttendanceSubmit}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleAttendanceChange}
        />
        <input
          name="surname"
          placeholder="Surname"
          value={form.surname}
          onChange={handleAttendanceChange}
        />
        <input
          name="studentNumber"
          placeholder="Student Number"
          value={form.studentNumber}
          onChange={handleAttendanceChange}
        />
        <input
          name="phone"
          placeholder="Phone"
          value={form.phone}
          onChange={handleAttendanceChange}
        />
        <input
          name="signIn"
          placeholder="Sign In Time"
          value={form.signIn}
          onChange={handleAttendanceChange}
        />
        <input
          name="signOut"
          placeholder="Sign Out Time"
          value={form.signOut}
          onChange={handleAttendanceChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Monitoring;
