import React, { useEffect, useState } from "react";
import API, { setAuth } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Monitoring() {
  const [data, setData] = useState([]);
  const [form, setForm] = useState({
    name: "",
    surname: "",
    studentNumber: "",
    phone: "",
    signIn: "",
    signOut: "",
  });

  const nav = useNavigate();
  const role = localStorage.getItem("role");

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Please login first.");
      nav("/login");
      return;
    }

    // allowed roles
    if (!["STUDENT", "LECTURER", "PL", "PRL"].includes(role)) {
      alert("Access denied.");
      nav("/");
      return;
    }

    setAuth(token);
    fetchMonitoring();
  }, [nav]);

  async function fetchMonitoring() {
    try {
      // ✅ fixed endpoint
      const res = await API.get("/api/monitoring");
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch monitoring data");
    }
  }

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    // validate student number (exactly 9 digits)
    if (!/^\d{9}$/.test(form.studentNumber)) {
      alert("Student number must be exactly 9 digits");
      return;
    }

    try {
      // ✅ fixed endpoint
      await API.post("/api/attendance", form);
      alert("Attendance submitted successfully!");
      setForm({
        name: "",
        surname: "",
        studentNumber: "",
        phone: "",
        signIn: "",
        signOut: "",
      });

      // refresh monitoring data after submit
      fetchMonitoring();
    } catch (err) {
      console.error(err);
      alert("Failed to submit attendance");
    }
  }

  return (
    <div className="col-md-10 offset-md-1 mt-4">
      <h3>Monitoring</h3>
      {data.length === 0 ? (
        <p>No monitoring data available.</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Course</th>
              <th>Class</th>
              <th>Lecturer</th>
              <th>Students Present</th>
              <th>Total Registered</th>
              <th>Performance</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, i) => (
              <tr key={i}>
                <td>{item.courseName}</td>
                <td>{item.className}</td>
                <td>{item.lecturerName}</td>
                <td>{item.actualStudentsPresent}</td>
                <td>{item.totalRegisteredStudents}</td>
                <td>{item.performance || "N/A"}</td>
                <td>{new Date(item.dateOfLecture).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Attendance form only for STUDENT */}
      {role === "STUDENT" && (
        <div className="mt-4">
          <h4>Attendance Sign-in / Sign-out</h4>
          <form onSubmit={handleSubmit}>
            <div className="mb-2">
              <label>Name</label>
              <input
                name="name"
                value={form.name}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-2">
              <label>Surname</label>
              <input
                name="surname"
                value={form.surname}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-2">
              <label>Student Number (9 digits)</label>
              <input
                name="studentNumber"
                value={form.studentNumber}
                onChange={handleChange}
                className="form-control"
                maxLength="9"
                required
              />
            </div>
            <div className="mb-2">
              <label>Phone Number</label>
              <input
                name="phone"
                value={form.phone}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-2">
              <label>Signature (Sign-In)</label>
              <input
                name="signIn"
                value={form.signIn}
                onChange={handleChange}
                className="form-control"
                placeholder="Type your name or initials"
                required
              />
            </div>
            <div className="mb-2">
              <label>Signature (Sign-Out)</label>
              <input
                name="signOut"
                value={form.signOut}
                onChange={handleChange}
                className="form-control"
                placeholder="Type your name or initials"
              />
            </div>
            <button className="btn btn-primary mt-2">Submit Attendance</button>
          </form>
        </div>
      )}
    </div>
  );
}
