import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function Dashboard() {
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalReports: 0,
    totalClasses: 0,
    loading: true
  });

  // Fetch statistics from backend
  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');

      // Fetch courses count
      const coursesRes = await axios.get('http://localhost:4000/api/courses', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Fetch reports count
      const reportsRes = await axios.get('http://localhost:4000/api/reports', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Fetch classes count
      const classesRes = await axios.get('http://localhost:4000/api/classes', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setStats({
        totalCourses: coursesRes.data.length,
        totalReports: reportsRes.data.length,
        totalClasses: classesRes.data.length,
        loading: false
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
      setStats(prev => ({ ...prev, loading: false }));
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  // Inline Styles
  const styles = {
    dashboardContainer: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    },
    statsRow: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '20px',
      marginBottom: '30px'
    },
    statCard: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '12px',
      padding: '25px',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      cursor: 'pointer'
    },
    statCardHover: {
      transform: 'translateY(-5px)',
      boxShadow: '0 8px 25px rgba(0, 0, 0, 0.15)'
    },
    statIcon: {
      fontSize: '2.5rem',
      marginRight: '20px'
    },
    statContent: {
      flex: '1'
    },
    statTitle: {
      margin: '0 0 8px 0',
      fontSize: '1rem',
      fontWeight: '500',
      opacity: '0.9'
    },
    statNumber: {
      margin: '0',
      fontSize: '2rem',
      fontWeight: 'bold'
    },
    dashboardCard: {
      background: 'linear-gradient(135deg, #2c3e50 0%, #3498db 100%)',
      borderRadius: '12px',
      padding: '40px',
      color: 'white',
      textAlign: 'center',
      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)'
    },
    dashboardTitle: {
      margin: '0 0 20px 0',
      fontSize: '2rem',
      fontWeight: '600'
    },
    dashboardText: {
      margin: '15px 0',
      fontSize: '1.1rem',
      lineHeight: '1.6',
      opacity: '0.9'
    },
    highlightText: {
      color: '#f39c12',
      fontWeight: 'bold'
    },
    registerLink: {
      color: '#f3f5f6',
      fontWeight: 'bold',
      textDecoration: 'none'
    },
    loadingText: {
      textAlign: 'center',
      padding: '20px',
      fontSize: '1.1rem',
      color: '#666'
    },
    // Mobile responsive styles
    mobileStyles: {
      '@media (max-width: 768px)': {
        dashboardContainer: {
          padding: '15px'
        },
        statsRow: {
          gridTemplateColumns: '1fr',
          gap: '15px'
        },
        statCard: {
          padding: '20px'
        },
        statIcon: {
          fontSize: '2rem',
          marginRight: '15px'
        },
        statNumber: {
          fontSize: '1.5rem'
        },
        dashboardCard: {
          padding: '25px'
        },
        dashboardTitle: {
          fontSize: '1.5rem'
        }
      }
    }
  };

  // Hover state management
  const [hoveredCard, setHoveredCard] = useState(null);

  return (
    <div style={styles.dashboardContainer}>
      {/* Statistics Cards Row */}
      <div style={styles.statsRow}>
        {/* Total Courses Card */}
        <div 
          style={{
            ...styles.statCard,
            ...(hoveredCard === 'courses' ? styles.statCardHover : {})
          }}
          onMouseEnter={() => setHoveredCard('courses')}
          onMouseLeave={() => setHoveredCard(null)}
        >
          <div style={styles.statIcon}>üìö</div>
          <div style={styles.statContent}>
            <h3 style={styles.statTitle}>Total Courses</h3>
            <p style={styles.statNumber}>
              {stats.loading ? 'Loading...' : stats.totalCourses}
            </p>
          </div>
        </div>

        {/* Total Reports Card */}
        <div 
          style={{
            ...styles.statCard,
            ...(hoveredCard === 'reports' ? styles.statCardHover : {})
          }}
          onMouseEnter={() => setHoveredCard('reports')}
          onMouseLeave={() => setHoveredCard(null)}
        >
          <div style={styles.statIcon}>üìã</div>
          <div style={styles.statContent}>
            <h3 style={styles.statTitle}>Total Reports</h3>
            <p style={styles.statNumber}>
              {stats.loading ? 'Loading...' : stats.totalReports}
            </p>
          </div>
        </div>

        {/* Total Classes Card */}
        <div 
          style={{
            ...styles.statCard,
            ...(hoveredCard === 'classes' ? styles.statCardHover : {})
          }}
          onMouseEnter={() => setHoveredCard('classes')}
          onMouseLeave={() => setHoveredCard(null)}
        >
          <div style={styles.statIcon}>üè´</div>
          <div style={styles.statContent}>
            <h3 style={styles.statTitle}>Total Classes</h3>
            <p style={styles.statNumber}>
              {stats.loading ? 'Loading...' : stats.totalClasses}
            </p>
          </div>
        </div>
      </div>

      {/* Welcome Card */}
      <div style={styles.dashboardCard}>
        <h2 style={styles.dashboardTitle}>LUCT Lecture Reporting System</h2>
        <p style={styles.dashboardText}>
          Submit and review lecture reports easily. Use the Register option to set up accounts for students, lecturers, PRLs, or PLs.
        </p>
        <p style={styles.dashboardText}>
          Default administrator login: <span style={styles.highlightText}>admin@luct.edu</span> / <span style={styles.highlightText}>password</span> (role: PL)
        </p>

        {/* Register link */}
        <p style={styles.dashboardText}>
          Don't you have an account?{" "}
          <Link to="/register" style={styles.registerLink}>
            Register here
          </Link>
        </p>
      </div>
    </div>
  );
}
