import React, { useEffect, useState } from "react";
import API, { setAuth } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Monitoring() {
  const [data, setData] = useState([]);
  const nav = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    if (!token) {
      alert("Please login first.");
      nav("/login");
      return;
    }

    // allowed roles
    if (!["STUDENT", "LECTURER", "PL", "PRL"].includes(role)) {
      alert("Access denied.");
      nav("/");
      return;
    }

    setAuth(token);
    fetchMonitoring();
  }, [nav]);

  async function fetchMonitoring() {
    try {
      const res = await API.get("/monitoring");
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch monitoring data");
    }
  }

  return (
    <div className="col-md-10 offset-md-1 mt-4">
      <h3>Monitoring</h3>
      {data.length === 0 ? (
        <p>No monitoring data available.</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Course</th>
              <th>Class</th>
              <th>Lecturer</th>
              <th>Students Present</th>
              <th>Total Registered</th>
              <th>Performance</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, i) => (
              <tr key={i}>
                <td>{item.courseName}</td>
                <td>{item.className}</td>
                <td>{item.lecturerName}</td>
                <td>{item.actualStudentsPresent}</td>
                <td>{item.totalRegisteredStudents}</td>
                <td>{item.performance || "N/A"}</td>
                <td>{new Date(item.dateOfLecture).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
