import React, { useEffect, useState } from "react";
import API from "../services/api";

function Monitoring() {
  const [monitoringData, setMonitoringData] = useState([]);
  const [notes, setNotes] = useState({}); // store notes per lecturer/course
  const [form, setForm] = useState({
    name: "",
    surname: "",
    studentNumber: "",
    phone: "",
    signIn: "",
    signOut: "",
  });

  // ✅ Fetch monitoring data
  const fetchMonitoringData = () => {
    API.get("/monitoring")
      .then((res) => setMonitoringData(res.data))
      .catch((err) => console.error("Monitoring fetch error:", err));
  };

  useEffect(() => {
    fetchMonitoringData();
  }, []);

  // ✅ Handle input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ✅ Handle notes change
  const handleNoteChange = (idx, value) => {
    setNotes({ ...notes, [idx]: value });
  };

  // ✅ Submit attendance
  const handleSubmit = (e) => {
    e.preventDefault();
    API.post("/attendance", form)
      .then(() => {
        alert("Attendance submitted ✅");
        setForm({
          name: "",
          surname: "",
          studentNumber: "",
          phone: "",
          signIn: "",
          signOut: "",
        });
        fetchMonitoringData(); // refresh data
      })
      .catch((err) => console.error("Attendance submit error:", err));
  };

  return (
    <div>
      <h2>📊 Monitoring Data</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Lecturer</th>
            <th>Total Students</th>
            <th>Present</th>
            <th>Notes</th>
          </tr>
        </thead>
        <tbody>
          {monitoringData.length > 0 ? (
            monitoringData.map((item, idx) => (
              <React.Fragment key={idx}>
                <tr>
                  <td>{item.courseName}</td>
                  <td>{item.lecturerName}</td>
                  <td>{item.totalRegisteredStudents}</td>
                  <td>{item.actualStudentsPresent}</td>
                  <td rowSpan="2" style={{ verticalAlign: "top" }}>
                    <textarea
                      rows="3"
                      cols="30"
                      placeholder="Write notes here..."
                      value={notes[idx] || ""}
                      onChange={(e) => handleNoteChange(idx, e.target.value)}
                    />
                  </td>
                </tr>
                <tr>
                  <td colSpan="4" style={{ fontStyle: "italic", color: "gray" }}>
                    Extra row under lecturer/student info
                  </td>
                </tr>
              </React.Fragment>
            ))
          ) : (
            <tr>
              <td colSpan="5">No monitoring data available</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>📝 Submit Attendance</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
        />
        <input
          name="surname"
          placeholder="Surname"
          value={form.surname}
          onChange={handleChange}
        />
        <input
          name="studentNumber"
          placeholder="Student Number"
          value={form.studentNumber}
          onChange={handleChange}
        />
        <input
          name="phone"
          placeholder="Phone"
          value={form.phone}
          onChange={handleChange}
        />
        <input
          name="signIn"
          placeholder="Sign In Time"
          value={form.signIn}
          onChange={handleChange}
        />
        <input
          name="signOut"
          placeholder="Sign Out Time"
          value={form.signOut}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Monitoring;
