import React, { useState, useEffect } from 'react';
import '../styles/dashboard.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function Dashboard() {
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalReports: 0,
    totalClasses: 0,
    loading: true
  });

  // Fetch statistics from backend
  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');

      // Fetch courses count
      const coursesRes = await axios.get('http://localhost:4000/api/courses', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Fetch reports count (adjust URL based on your reports endpoint)
      const reportsRes = await axios.get('http://localhost:4000/api/reports', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Fetch classes count (adjust URL based on your classes endpoint)
      const classesRes = await axios.get('http://localhost:4000/api/classes', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setStats({
        totalCourses: coursesRes.data.length,
        totalReports: reportsRes.data.length,
        totalClasses: classesRes.data.length,
        loading: false
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
      setStats(prev => ({ ...prev, loading: false }));
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  return (
    <div className="dashboard-container">
      {/* Statistics Cards Row */}
      <div className="stats-row">
        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-content">
            <h3>Total Courses</h3>
            <p className="stat-number">
              {stats.loading ? 'Loading...' : stats.totalCourses}
            </p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìã</div>
          <div className="stat-content">
            <h3>Total Reports</h3>
            <p className="stat-number">
              {stats.loading ? 'Loading...' : stats.totalReports}
            </p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-content">
            <h3>Total Classes</h3>
            <p className="stat-number">
              {stats.loading ? 'Loading...' : stats.totalClasses}
            </p>
          </div>
        </div>
      </div>

      {/* Welcome Card */}
      <div className="dashboard-card">
        <h2>LUCT Lecture Reporting System</h2>
        <p>Submit and review lecture reports easily. Use the Register option to set up accounts for students, lecturers, PRLs, or PLs.</p>
        <p>Default administrator login: <strong>admin@luct.edu</strong> / <strong>password</strong> (role: PL)</p>

        {/* ‚úÖ Question + Register link inside the card */}
        <p>
          Don't you have an account?{" "}
          <Link to="/register" style={{ color: "#f3f5f6ff", fontWeight: "bold" }}>
            Register here
          </Link>
        </p>
      </div>
    </div>
  );
}
