// frontend/src/pages/Course.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:4000/courses"; // adjust if needed
const USERS_API = "http://localhost:4000/users"; // endpoint to fetch users

const Course = ({ userRole }) => {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    courseCode: "",
    courseName: "",
    facultyName: "",
    stream: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [lecturers, setLecturers] = useState([]);
  const [selectedLecturers, setSelectedLecturers] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState("");

  // Fetch courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses(res.data);
    } catch (err) {
      console.error("Fetch courses error:", err);
    }
  };

  // Fetch lecturers
  const fetchLecturers = async () => {
    try {
      const res = await axios.get(USERS_API, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      // Only include lecturers
      setLecturers(res.data.filter(u => u.role === "LECTURER"));
    } catch (err) {
      console.error("Fetch lecturers error:", err);
    }
  };

  useEffect(() => {
    fetchCourses();
    if (userRole === "PL") fetchLecturers();
  }, []);

  // Handle course form change
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Add or update course
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        const res = await axios.put(`${API_URL}/${editingId}`, formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setCourses(courses.map(c => (c.id === editingId ? res.data : c)));
        setEditingId(null);
      } else {
        const res = await axios.post(API_URL, formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setCourses([...courses, res.data]);
      }
      setFormData({ courseCode: "", courseName: "", facultyName: "", stream: "" });
    } catch (err) {
      console.error("Course submit error:", err);
    }
  };

  // Edit course
  const editCourse = (course) => {
    setEditingId(course.id);
    setFormData({
      courseCode: course.courseCode,
      courseName: course.courseName,
      facultyName: course.facultyName,
      stream: course.stream || "",
    });
  };

  // Delete course
  const deleteCourse = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses(courses.filter(c => c.id !== id));
    } catch (err) {
      console.error("Delete course error:", err);
    }
  };

  // Assign lecturers using dropdown
  const assignLecturers = async () => {
    if (!selectedCourseId || selectedLecturers.length === 0) return alert("Select course and lecturers.");
    try {
      await axios.post(
        `${API_URL}/${selectedCourseId}/assign`,
        { lecturerIds: selectedLecturers },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("Lecturers assigned successfully!");
      setSelectedLecturers([]);
      setSelectedCourseId("");
      fetchCourses(); // refresh
    } catch (err) {
      console.error("Assign lecturers error:", err);
    }
  };

  return (
    <div>
      <h2>Courses</h2>

      {/* PL Only: Add/Edit Form */}
      {userRole === "PL" && (
        <div style={{ marginBottom: "30px" }}>
          <h3>{editingId ? "Edit Course" : "Add New Course"}</h3>
          <form onSubmit={handleSubmit}>
            <input
              name="courseCode"
              placeholder="Course Code"
              value={formData.courseCode}
              onChange={handleChange}
              required
            />
            <input
              name="courseName"
              placeholder="Course Name"
              value={formData.courseName}
              onChange={handleChange}
              required
            />
            <input
              name="facultyName"
              placeholder="Faculty"
              value={formData.facultyName}
              onChange={handleChange}
              required
            />
            <input
              name="stream"
              placeholder="Stream"
              value={formData.stream}
              onChange={handleChange}
            />
            <button type="submit">{editingId ? "Update Course" : "Add Course"}</button>
            {editingId && <button type="button" onClick={() => { setEditingId(null); setFormData({ courseCode: "", courseName: "", facultyName: "", stream: "" }); }}>Cancel</button>}
          </form>
        </div>
      )}

      {/* Courses Table */}
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Faculty</th>
            <th>Stream</th>
            <th>Lecturers</th>
            {userRole === "PL" && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>{course.id}</td>
              <td>{course.courseCode}</td>
              <td>{course.courseName}</td>
              <td>{course.facultyName}</td>
              <td>{course.stream || "-"}</td>
              <td>{course.lecturers.map(l => l.name).join(", ")}</td>
              {userRole === "PL" && (
                <td>
                  <button onClick={() => editCourse(course)}>Edit</button>
                  <button onClick={() => deleteCourse(course.id)}>Delete</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>

      {/* PL Only: Assign Lecturers with Dropdown */}
      {userRole === "PL" && (
        <div style={{ marginTop: "20px" }}>
          <h3>Assign Lecturers to Course</h3>
          <select value={selectedCourseId} onChange={(e) => setSelectedCourseId(e.target.value)}>
            <option value="">Select Course</option>
            {courses.map(c => (
              <option key={c.id} value={c.id}>{c.courseCode} - {c.courseName}</option>
            ))}
          </select>
          <select
            multiple
            value={selectedLecturers}
            onChange={(e) =>
              setSelectedLecturers(
                Array.from(e.target.selectedOptions, option => parseInt(option.value))
              )
            }
          >
            {lecturers.map(l => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>
          <button onClick={assignLecturers}>Assign Lecturers</button>
        </div>
      )}
    </div>
  );
};

export default Course;
