import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Classes() {
  const [classes, setClasses] = useState([]);
  const [courseName, setCourseName] = useState("");
  const lecturerId = localStorage.getItem("userId"); // ðŸ‘ˆ make sure this exists

  // âœ… Fetch classes
  useEffect(() => {
    if (!lecturerId) return;
    axios
      .get(`http://localhost:4000/api/classes/${lecturerId}`) // ðŸ‘ˆ fixed
      .then((res) => setClasses(res.data))
      .catch((err) => console.error("Fetch classes error:", err));
  }, [lecturerId]);

  // âœ… Add class
  const addClass = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:4000/api/classes", { // ðŸ‘ˆ fixed
        courseName,
        lecturerId,
      });
      setCourseName("");
      const res = await axios.get(`http://localhost:4000/api/classes/${lecturerId}`); // ðŸ‘ˆ fixed
      setClasses(res.data);
    } catch (err) {
      console.error("Add class error:", err);
    }
  };

  // âœ… Delete class
  const deleteClass = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/classes/${id}`); // ðŸ‘ˆ fixed
      setClasses(classes.filter((cls) => cls.id !== id));
    } catch (err) {
      console.error("Delete class error:", err);
    }
  };

  return (
    <div>
      <h2>My Classes</h2>
      <form onSubmit={addClass}>
        <input
          type="text"
          value={courseName}
          onChange={(e) => setCourseName(e.target.value)}
          placeholder="Course name"
          required
        />
        <button type="submit">Add Class</button>
      </form>

      <ul>
        {classes.map((cls) => (
          <li key={cls.id}>
            {cls.courseName}{" "}
            <button onClick={() => deleteClass(cls.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
