import React, { useEffect, useState } from "react";
import API from "../services/api";

function Classes({ lecturerId }) {
  const [classes, setClasses] = useState([]);
  const [newClass, setNewClass] = useState("");

  // Fetch lecturer's classes
  const fetchClasses = () => {
    API.get(`/classes/${lecturerId}`)
      .then((res) => setClasses(res.data))
      .catch((err) => console.error("Fetch classes error:", err));
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  // Add class
  const handleAddClass = (e) => {
    e.preventDefault();
    if (!newClass.trim()) return;

    API.post("/classes", { courseName: newClass, lecturerId })
      .then(() => {
        setNewClass("");
        fetchClasses();
      })
      .catch((err) => console.error("Add class error:", err));
  };

  // Delete class
  const handleDeleteClass = (id) => {
    API.delete(`/classes/${id}`)
      .then(() => fetchClasses())
      .catch((err) => console.error("Delete class error:", err));
  };

  return (
    <div className="card">
      <h2 style={{ padding: "10px 0" }}>üìö My Classes</h2>

      <form onSubmit={handleAddClass} style={{ marginBottom: "15px" }}>
        <input
          type="text"
          value={newClass}
          onChange={(e) => setNewClass(e.target.value)}
          placeholder="Enter class/course name"
          style={{ padding: "6px", marginRight: "10px", width: "250px" }}
        />
        <button
          type="submit"
          style={{
            padding: "6px 12px",
            borderRadius: "6px",
            border: "none",
            background: "linear-gradient(to right, #11998e, #38ef7d)",
            color: "white",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          ‚ûï Add Class
        </button>
      </form>

      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          background: "white",
          borderRadius: "10px",
          overflow: "hidden",
        }}
      >
        <thead style={{ background: "linear-gradient(to right, #11998e, #38ef7d)", color: "white" }}>
          <tr>
            <th style={{ padding: "10px", textAlign: "left" }}>Course Name</th>
            <th style={{ padding: "10px", textAlign: "center" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {classes.length > 0 ? (
            classes.map((cls) => (
              <tr key={cls.id}>
                <td style={{ padding: "8px" }}>{cls.courseName}</td>
                <td style={{ textAlign: "center" }}>
                  <button
                    onClick={() => handleDeleteClass(cls.id)}
                    style={{
                      padding: "6px 12px",
                      borderRadius: "6px",
                      border: "none",
                      background: "linear-gradient(to right, #ff512f, #dd2476)",
                      color: "white",
                      cursor: "pointer",
                      fontWeight: "bold",
                    }}
                  >
                    ‚ùå Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="2" style={{ textAlign: "center", padding: "10px", color: "gray" }}>
                No classes added yet
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Classes;
