// frontend/src/pages/Course.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:4000/courses"; 
const USERS_API = "http://localhost:4000/users"; 

const Course = ({ userRole }) => {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    courseCode: "",
    courseName: "",
    facultyName: "",
    stream: "",
  });
  const [lecturers, setLecturers] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState("");
  const [selectedLecturers, setSelectedLecturers] = useState([]);

  // Fetch courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch lecturers (PL only)
  const fetchLecturers = async () => {
    try {
      const res = await axios.get(USERS_API, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setLecturers(res.data.filter(u => u.role === "LECTURER"));
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchCourses();
    if (userRole === "PL") fetchLecturers();
  }, []);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // PL: Add course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(API_URL, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setCourses([...courses, res.data]);
      setFormData({ courseCode: "", courseName: "", facultyName: "", stream: "" });
      alert("Course added successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to add course");
    }
  };

  // PL: Assign lecturers
  const assignLecturers = async () => {
    if (!selectedCourseId || selectedLecturers.length === 0) return alert("Select course and lecturers.");
    try {
      await axios.post(`${API_URL}/${selectedCourseId}/assign`, { lecturerIds: selectedLecturers }, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("Lecturers assigned successfully!");
      setSelectedCourseId("");
      setSelectedLecturers([]);
      fetchCourses();
    } catch (err) {
      console.error(err);
      alert("Failed to assign lecturers");
    }
  };

  // Inline styles
  const styles = {
    container: { padding: "20px", maxWidth: "900px", margin: "auto", fontFamily: "Arial, sans-serif" },
    formBox: { marginBottom: "30px", padding: "15px", border: "1px solid #ddd", borderRadius: "8px", backgroundColor: "#f9f9f9" },
    input: { display: "block", margin: "8px 0", padding: "8px", width: "100%", maxWidth: "400px" },
    button: { marginTop: "10px", padding: "8px 16px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" },
    buttonHover: { backgroundColor: "#0056b3" },
    table: { width: "100%", borderCollapse: "collapse" },
    thtd: { padding: "10px", border: "1px solid #ddd", textAlign: "left" },
    th: { backgroundColor: "#f2f2f2" },
    assignBox: { marginTop: "20px", padding: "15px", border: "1px solid #ddd", borderRadius: "8px", backgroundColor: "#f9f9f9" }
  };

  return (
    <div style={styles.container}>
      <h2>Courses</h2>

      {/* PL Only: Add Course Form */}
      {userRole === "PL" && (
        <div style={styles.formBox}>
          <h3>Add New Course</h3>
          <form onSubmit={handleAddCourse}>
            <input
              type="text"
              name="courseCode"
              placeholder="Course Code"
              value={formData.courseCode}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              type="text"
              name="courseName"
              placeholder="Course Name"
              value={formData.courseName}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              type="text"
              name="facultyName"
              placeholder="Faculty"
              value={formData.facultyName}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              type="text"
              name="stream"
              placeholder="Stream (optional)"
              value={formData.stream}
              onChange={handleChange}
              style={styles.input}
            />
            <button type="submit" style={styles.button}>Add Course</button>
          </form>
        </div>
      )}

      {/* Courses Table (all roles can view) */}
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={{...styles.thtd, ...styles.th}}>ID</th>
            <th style={styles.thtd}>Course Code</th>
            <th style={styles.thtd}>Course Name</th>
            <th style={styles.thtd}>Faculty</th>
            <th style={styles.thtd}>Stream</th>
            <th style={styles.thtd}>Lecturers</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(c => (
            <tr key={c.id}>
              <td style={styles.thtd}>{c.id}</td>
              <td style={styles.thtd}>{c.courseCode}</td>
              <td style={styles.thtd}>{c.courseName}</td>
              <td style={styles.thtd}>{c.facultyName}</td>
              <td style={styles.thtd}>{c.stream || "-"}</td>
              <td style={styles.thtd}>{c.lecturers.map(l => l.name).join(", ")}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* PL Only: Assign Lecturers */}
      {userRole === "PL" && (
        <div style={styles.assignBox}>
          <h3>Assign Lecturers to Course</h3>
          <select value={selectedCourseId} onChange={(e) => setSelectedCourseId(e.target.value)} style={styles.input}>
            <option value="">Select Course</option>
            {courses.map(c => (
              <option key={c.id} value={c.id}>{c.courseCode} - {c.courseName}</option>
            ))}
          </select>
          <select
            multiple
            value={selectedLecturers}
            onChange={(e) =>
              setSelectedLecturers(
                Array.from(e.target.selectedOptions, o => parseInt(o.value))
              )
            }
            style={styles.input}
          >
            {lecturers.map(l => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>
          <button onClick={assignLecturers} style={styles.button}>Assign Lecturers</button>
        </div>
      )}
    </div>
  );
};

export default Course;
